import { ArrowBackIos, ArrowForwardIos } from "@material-ui/icons";
import styled from "styled-components";
import SliderImg from "./img/slider-img-1.png";
const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
`;
const Arrow = styled.div`
  height: 50px;
  width: 50px;
  border-radius: 50%;
  background-color: #fff7f7;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto;
  cursor: pointer;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100%;
  position: absolute;
`;
const Slide = styled.div`
  display: flex;
  align-items: center;
  width: 100vw;
  height: 100vh;
`;
const ImgContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Img = styled.div`
  height: 80%;
  background-image: url(${img});
`;

const InfoContainer = styled.div`
  flex: 1;
`;

function Sliders() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowBackIos style={{ fontSize: "18px" }} />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Img img={SliderImg} />
          </ImgContainer>
          <InfoContainer></InfoContainer>
        </Slide>
        {/* <Slide>
          <ImgContainer>
            <Img src="../../public/img/slider-img-1.png" />
          </ImgContainer>
          <InfoContainer></InfoContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Img src="../../public/img/slider-img-1.png" />
          </ImgContainer>
          <InfoContainer></InfoContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Img src="../../public/img/slider-img-1.png" />
          </ImgContainer>
          <InfoContainer></InfoContainer>
        </Slide> */}
      </Wrapper>
      <Arrow direction="right">
        <ArrowForwardIos style={{ fontSize: "18px" }} />
      </Arrow>
    </Container>
  );
}

export default Sliders;
