import { productsData } from "../data";
import Product from "./Product";
import styled from "styled-components";
import { mobile } from "../responsive";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import axios from "axios";
import { Sort } from "@material-ui/icons";
const Container = styled.div`
  max-width: 100vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  align-items: center;
  height: auto;
  max-height: 600vh;
  position: relative;
  ${mobile({
    flexWrap: "nowrap",
    flexDirection: "column",
    marginBottom: "50px",
  })}
`;

function Products({ filters, sort }) {
  const location = useLocation();
  const cat = location.search.split("=")[1];
  const [filterProduct, setFilterProduct] = useState([]);
  const [product, setProduct] = useState([]);
  useEffect(async () => {
    try {
      const data = await axios.get(
        cat
          ? `http://localhost:5000/api/products?category=${cat}`
          : "http://localhost:5000/api/products"
      );
      setProduct(data.data);
    } catch (error) {
      console.log(error);
    }
  }, [cat]);
  useEffect(() => {
    const productFilter = product.filter((item) => {
      if (filters.color && filters.size) {
        const coMatch = item.color === filters.color;
        const siMatch = item.size === filters.size;
        return coMatch && siMatch;
      } else if (filters.color || filters.size) {
        const coMatch = item.color === filters.color;
        const siMatch = item.size === filters.size;
        return coMatch || siMatch;
      }
    });
  }, [cat, filters]);
  // console.log(filters, cat, sort);
  // console.log(product);
  // console.log(filters.size || filters.color);

  return (
    <Container>
      {product.map((item) => (
        <Product key={item.id} item={item} />
      ))}
    </Container>
  );
}

export default Products;
