import {
  ArrowBackIos,
  ArrowForward,
  ArrowForwardIos,
} from "@material-ui/icons";
import styled from "styled-components";
import SliderImg1 from "./img/slider-img-1.png";
import SliderImg2 from "./img/slider-img-2.png";
import SliderImg3 from "./img/slider-img-3.png";
import SliderImg4 from "./img/slider-img-4.png";
const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
`;
const Arrow = styled.div`
  height: 50px;
  width: 50px;
  border-radius: 50%;
  background-color: #fff7f7;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  margin: auto;
  cursor: pointer;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  opacity: 0.5;
`;

const Wrapper = styled.div`
  height: 100%;
  position: absolute;
  background-color: #f6f9fe;
`;
const Slide = styled.div`
  display: flex;
  align-items: center;
  width: 100vw;
  height: 100vh;
`;
const ImgContainer = styled.div`
  flex: 1;
  height: 100%;
`;

const Img = styled.div`
  height: 80%;
  background-image: url(${(props) => props.img});
  width: 410px;
  /* border-radius: 50%; */
  background-position: center;
  background-size: cover;
  /* object-fit: cover; */
`;

const InfoContainer = styled.div`
  flex: 1;
  padding: 50px;
`;

const Title = styled.h1`
  font-size: 80px;
  font-weight: 500;
  padding-bottom: 35px;
`;
const SubTitle = styled.h2`
  font-weight: 300;
  font-size: 30px;
`;
const Btn = styled.button`
  outline: none;
  border: none;
  cursor: pointer;
  padding: 10px 15px;
  border: 1px solid black;
  display: flex;
  justify-content: center;
  align-items: center;
`;

function Sliders() {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowBackIos style={{ fontSize: "18px" }} />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Img img={SliderImg2} />
          </ImgContainer>
          <InfoContainer>
            <Title>BLACK FRIDAY SALE</Title>
            <SubTitle>BECOME A HANDSOME MAN WITH 60% SALE OFF</SubTitle>
            <Btn>
              Shop Now{" "}
              <ArrowForward style={{ fontSize: "16px", marginLeft: "10px" }} />
            </Btn>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right">
        <ArrowForwardIos style={{ fontSize: "18px" }} />
      </Arrow>
    </Container>
  );
}

export default Sliders;
