import Navbar from "../components/Navbar";
import Announcement from "../components/Announcement";
import Feedback from "../components/Feedback";
import Footer from "../components/Footer";
import styled from "styled-components";
import { productsData } from "../data";

const Container = styled.div``;
const Wrapper = styled.div`
  max-width: 100vw;
  height: 100vh;
  background-color: #f6f9fe;
`;
const ImgContainer = styled.div``;
const Img = styled.img``;
const InfoContainer = styled.div``;
const Title = styled.h2``;
const SubTitle = styled.h3``;
const Price = styled.h1``;

function SingleProduct() {
  return (
    <Container>
      <Announcement />
      <Navbar />
      <Wrapper>
        <ImgContainer>
          <Img src={productsData[0].img} />
        </ImgContainer>
        <InfoContainer>
          <Title>{productsData[0].title}</Title>
          <SubTitle>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Animi vel
            excepturi voluptates, nihil nesciunt error nam et voluptate rem
            atque unde laboriosam molestias possimus veniam delectus dolore
            maxime odit quod?
          </SubTitle>
          <Price>{productsData[0].price}</Price>
          <Size>
            SIZE
            <Select>
              <Option disabled>Color</Option>
              <Option>Red</Option>
              <Option>Blue</Option>
              <Option>White</Option>
              <Option>Black</Option>
              <Option>Green</Option>
              <Option>Yellow</Option>
            </Select>
          </Size>
        </InfoContainer>
      </Wrapper>
      <Feedback />
      <Footer />
    </Container>
  );
}

export default SingleProduct;
