import styled from "styled-components";
import Announcement from "../components/Announcement";
import Footer from "../components/Footer";
import Navbar from "../components/Navbar";

const Container = styled.div``;
const Wrapper = styled.div`
  padding: 20px;
`;
const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`;
const Top = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;
const TopButton = styled.button`
  outline: none;
  border: none;
  padding: 15px 30px;
  border: 1px solid black;
  background-color: ${(props) => props.bg};
  color: ${(props) => props.color};
  font-size: 15px;
  font-weight: 600;
  box-shadow: 10px 10px 5px -2px rgba(209, 199, 199, 0.75);
  -webkit-box-shadow: 10px 10px 5px -2px rgba(209, 199, 199, 0.75);
  -moz-box-shadow: 10px 10px 5px -2px rgba(209, 199, 199, 0.75);
`;
const TopTexts = styled.div``;
const TopText = styled.span`
  text-decoration: underline;
  cursor: pointer;
  margin: 0 12px;
`;

const Bottom = styled.div``;

function Cart() {
  return (
    <Container>
      <Navbar />
      <Announcement />
      <Wrapper>
        <Title>YOUR CART</Title>
        <Top>
          <TopButton bg="transparent" color="black">
            KEEP SHOPPING
          </TopButton>
          <TopTexts>
            <TopText>Shopping Cart(2)</TopText>
            <TopText>Your Wishlist</TopText>
          </TopTexts>
          <TopButton bg="black" color="white">
            CHECKOUT NOW
          </TopButton>
        </Top>
        <Bottom></Bottom>
      </Wrapper>
      <Footer />
    </Container>
  );
}

export default Cart;
