import React from "react";
import styled from "styled-components";
import { Search, LocalGroceryStoreOutlined } from "@material-ui/icons";
import { Badge } from "@material-ui/core";
// components
const Container = styled.div`
  height: 60px;
`;
const Wrapper = styled.div`
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;
const Left = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
`;
const Language = styled.span`
  font-size: 14px;
  cursor: pointer;
`;
const SearchContainer = styled.div`
  border: 1px solid lightgray;
  display: flex;
  align-items: center;
  margin-left: 25px;
  padding: 5px;
  width: 60%;
`;
const Input = styled.input`
  border: none;
`;
const Center = styled.div`
  flex: 1;
`;
const Logo = styled.h1`
  font-weight: bold;
  padding: 0;
  margin: 0;
  text-align: center;
`;
const Right = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: flex-end;
  padding-right: 15px;
`;
const FlexWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;
const FlexItem = styled.div`
  margin-left: 25px;
  cursor: pointer;
`;
function Navbar() {
  return (
    <Container>
      <Wrapper>
        <Left>
          <Language>EN</Language>
          <SearchContainer>
            <Search />
            <Input />
          </SearchContainer>
        </Left>
        <Center>
          <Logo>NBTAILEE.</Logo>
        </Center>
        <Right>
          {/* <FlexWrapper> */}
          <FlexItem>
            <a
              style={{ color: "black", textDecoration: "none" }}
              href="http://localhost:3000/"
            >
              REGISTER
            </a>
          </FlexItem>
          <FlexItem>
            <a
              style={{ color: "black", textDecoration: "none" }}
              href="http://localhost:3000/"
            >
              SIGN IN
            </a>
          </FlexItem>
          <FlexItem>
            <Badge badgeContent={4} color="primary">
              <LocalGroceryStoreOutlined />
            </Badge>
          </FlexItem>
          {/* </FlexWrapper> */}
        </Right>
      </Wrapper>
    </Container>
  );
}

export default Navbar;
