import styled from "styled-components";
const Container = styled.div`
  /* flex: 1; */
  height: 340px;
  width: 300px;
  margin: 30px 15px 20px 15px;
  border: 1px solid black;
`;
const ImgContainer = styled.div`
  height: 100%;
  width: 100%;
  position: relative;
`;
const Img = styled.img`
  height: 300px;
  width: 100%;
  object-fit: cover;
`;
const BtnGroup = styled.div`
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  margin: auto;
  margin: auto;
`;
const Btn = styled.button`
  outline: none;
  border: none;
`;
const Info = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;
const Title = styled.h2`
  color: black;
  font-weight: 500;
  font-size: 18px;
`;
const Price = styled.h3`
  color: gray;
  font-weight: 300;
  font-size: 15px;
`;

function Product({ item }) {
  return (
    <Container>
      <ImgContainer>
        <Img src={item.img} />
        <BtnGroup>
          <Btn>View More</Btn>
          <Btn>Buy Now</Btn>
        </BtnGroup>
      </ImgContainer>
      <Info>
        <Title>{item.title}</Title>
        <Price>{item.price}</Price>
      </Info>
    </Container>
  );
}

export default Product;
