import { productsData } from "../data";
import Product from "./Product";
import styled from "styled-components";
import { mobile } from "../responsive";
import { useEffect, useState } from "react";
import { useLocation } from "react-router";
import axios from "axios";
import { Sort } from "@material-ui/icons";
const Container = styled.div`
  max-width: 100vw;
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  align-items: center;
  height: auto;
  max-height: 600vh;
  position: relative;
  ${mobile({
    flexWrap: "nowrap",
    flexDirection: "column",
    marginBottom: "50px",
  })}
`;

function Products({ filters, sort }) {
  const location = useLocation();
  const cat = location.search.split("=")[1];
  const [filterProduct, setFilterProduct] = useState([]);
  const [product, setProduct] = useState([]);
  useEffect(async () => {
    try {
      const data = await axios.get(
        cat
          ? `http://localhost:5000/api/products?category=${cat}`
          : "http://localhost:5000/api/products"
      );
      setProduct(data.data);
    } catch (error) {
      console.log(error);
    }
  }, [cat]);
  useEffect(() => {
    cat &&
      setFilterProduct(
        product.filter((item) =>
          Object.entries(filters).every(([key, value]) =>
            item[key].includes(value)
          )
        )
      );
  }, [product, cat, filters]);
  // console.log(filters, cat, sort);
  // console.log(product);
  // console.log(filters.size || filters.color);
  useEffect(() => {
    if (sort === "newest") {
      setFilterProduct((prev) =>
        [...prev].sort((a, b) => a.createAt - b.createAt)
      );
    } else if (sort === "oldest") {
      setFilterProduct((prev) =>
        [...prev].sort((a, b) => b.createAt - a.createAt)
      );
    } else {
      setFilterProduct((prev) => [...prev].sort((a, b) => a.price - b.price));
    }
  }, [sort]);
  return (
    <Container>
      {filters
        ? filterProduct.map((item) => <Product key={item.id} item={item} />)
        : product.map((item) => <Product key={item.id} item={item} />)}
    </Container>
  );
}

export default Products;
