import Navbar from "../components/Navbar";
import Announcement from "../components/Announcement";
import Feedback from "../components/Feedback";
import Footer from "../components/Footer";
import styled from "styled-components";
import { productsData } from "../data";

const Container = styled.div``;
const Wrapper = styled.div`
  max-width: 100vw;
  height: 100vh;
  background-color: #f6f9fe;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;
const ImgContainer = styled.div`
  flex: 1;
  width: 50%;
  height: 70%;
  margin-left: 100px;
`;
const Img = styled.img`
  width: 80%;
  height: 100%;
  object-fit: cover;
`;
const InfoContainer = styled.div`
  flex: 1.2;
  padding: 0 50px;
`;
const Title = styled.h2`
  font-weight: 500;
  margin-bottom: 30px;
  margin-top: -250px;
  font-size: 40px;
  /* position: absolute; */
`;
const SubTitle = styled.h3`
  font-weight: 300;
  margin-bottom: 30px;
  font-size: 20px;
  width: 600px;
`;
const Price = styled.div`
  font-weight: 400;
  font-size: 40px;
  margin: 45px 0;
`;
const SizeColor = styled.div`
  font-size: 22px;
  display: flex;
  align-items: center;
  justify-content: space-around;
  width: 300px;
  font-weight: 500;
`;
const Select = styled.select`
  height: 100%;
  padding: 10px 20px;
  font-size: 17px;
`;
const Option = styled.option`
  height: 100%;
`;
const ColorContainer = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  width: 300px;
`;
const Color = styled.div`
  width: 25px;
  height: 25px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`;

function SingleProduct() {
  return (
    <Container>
      <Announcement />
      <Navbar />
      <Wrapper>
        <ImgContainer>
          <Img src={productsData[0].img} />
        </ImgContainer>
        <div style={{ flex: 0.1 }}></div>
        <InfoContainer>
          <Title>{productsData[0].title}</Title>
          <SubTitle>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Animi vel
            excepturi voluptates, nihil nesciunt error nam et voluptate rem
            atque unde laboriosam molestias possimus veniam delectus dolore
            maxime odit quod?
          </SubTitle>
          <Price>{productsData[0].price}</Price>
          <SizeColor>
            <ColorContainer>
              <div>Color</div>
              <Color color="gray"></Color>
              <Color color="#23212e"></Color>
              <Color color="black"></Color>
            </ColorContainer>
            <div style={{}}>SIZE</div>
            <Select>
              <Option disabled defaultChecked>
                Size
              </Option>
              <Option>S</Option>
              <Option>M</Option>
              <Option>L</Option>
              <Option>XL</Option>
            </Select>
          </SizeColor>
        </InfoContainer>
      </Wrapper>
      <Feedback />
      <Footer />
    </Container>
  );
}

export default SingleProduct;
