import { categoryData } from "../data";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 50vh;
  width: 100vw;
  position: relative;
  padding: 0;
  margin: 0;
`;

function Category() {
  return (
    <Container>
      {categoryData.map((item) => (
        <div
          style={{
            width: "100%",
            height: "100%",
            margin: "10px",
            padding: "0",
            flex: 1,
            position: "relative",
          }}
        >
          <Img src={item.img} />
          <Info>
            <Title>{item.title}</Title>
            <Btn>SHOW MORE</Btn>
          </Info>
        </div>
      ))}
    </Container>
  );
}

export default Category;
